    public static function getAllMemberIds($user) {
        $user_ids = $parent_ids = $contact_ids = [];

        $contacts_by_email = Contact::withTrashed()->where('email', $user->email)->get();

        if (!empty($contacts_by_email)) {
            foreach ($contacts_by_email as $email_contact) {
                if (!empty($email_contact->user_id)) {
                    $user_ids[] = $email_contact->user_id;
                }

                if (!empty($email_contact->parent_id)) {
                    $parent_ids[] = $email_contact->parent_id;
                }

                if (!empty($email_contact->contact_id)) {
                    $contact_ids[] = $email_contact->contact_id;
                }
            }
        }

        $ids_array_1 = $ids_array_2 = $ids_array_3 = [];

        if (!empty($user_ids)) {
            $ids_array_1 = Contact::withTrashed()->whereIn('user_id', $user_ids)->pluck('id')->toArray();
        }

        if (!empty($contact_ids)) {
            $ids_array_2 = Contact::withTrashed()->whereIn('parent_id', $contact_ids)->pluck('id')->toArray();
        }

        if (!empty($parent_ids)) {
            $ids_array_3 = Contact::withTrashed()->whereIn('contact_id', $parent_ids)->pluck('id')->toArray();
        }

        $member_ids = array_merge($ids_array_1, $ids_array_2, $ids_array_3);
        return $member_ids;
    }

    public static function switch_company($contact_id) {
        $user_id = Auth::id();
        $active_contact_id = $active_company = null;
        $new_register_contact = Contact::where('contact_id' , null)->where('user_id' , $user_id)->where('is_parent' , 1)->first();
        if (!empty($new_register_contact)) {
            $active_company = !empty($new_register_contact->company) ? $new_register_contact->company : $new_register_contact->firstName . ' ' . $new_register_contact->lastName;
            Session::put([
                'contact_id' => null,
                'company' => $active_company,
            ]);

        } else {
            $companies_count = Contact::where('user_id', $user_id)->get();
            if (count($companies_count) > 0) {
                if ($companies_count->count() == 1) {
                    $contact = Contact::where('contact_id', $contact_id)->where('status', '!=', 0)->first();
                    if (!empty($contact)) {
                        $active_contact_id = $contact->contact_id;
                        $active_company = !empty($contact->company) ? $contact->company : $contact->firstName . ' ' . $contact->lastName;
                    } 
                    else {
                        $contact = Contact::where('secondary_id', $contact_id)->where('status', '!=', 0)->first();
                        if (!empty($contact)) {
                            $active_contact_id = $contact->secondary_id;
                            $active_company = !empty($contact->company) ? $contact->company : $contact->firstName . ' ' . $contact->lastName;
                            
                        }
                    }
                }

                if ($companies_count->count() > 1) {
                    $contact = Contact::where('user_id', auth()->user()->id)->where('status', '!=', 0)->first();
                    if (!empty($contact)) {
                        $active_contact_id = null;
                        $active_company = null;
                    } 
                    else {
                        $contact = Contact::where('user_id', auth()->user()->id)->where('status', '!=', 0)->first();
                        if (!empty($contact)) {
                            $active_contact_id = null;
                            $active_company = null;
                        }
                    }
                }
            }  
            
            Session::put([
                'contact_id' => $active_contact_id,
                'company' => $active_company,
            ]);

            

            // $getSelectedContact = Contact::where('user_id', $user_id)
            // ->where(function ($query) use ($active_contact_id) {
            //     $query->where('contact_id', $active_contact_id)
            //         ->orWhere('secondary_id', $active_contact_id);
            // })
            // ->first();


            // Update all cart items with null contact_id to the selected company (active contact_id)
            Cart::where('user_id', $user_id)
            ->whereNull('contact_id')
            ->update(['contact_id' => $active_contact_id]);

            // Fetch the selected contact for the user
            $getSelectedContact = Contact::where('user_id', $user_id)
                ->where(function ($query) use ($active_contact_id) {
                    $query->where('contact_id', $active_contact_id)
                        ->orWhere('secondary_id', $active_contact_id);
                })
            ->first();

            if ($getSelectedContact) {
                // Retrieve cart items, including those with null contact_id
                $cartItems = Cart::where('user_id', $getSelectedContact->user_id)
                    ->where(function ($query) use ($active_contact_id) {
                        $query->where('contact_id', $active_contact_id)
                            ->orWhereNull('contact_id');
                    })
                    ->get();

                if ($cartItems->isNotEmpty()) {
                    // Group the cart items by product and option_id for merging quantities
                    $cartItemsGrouped = $cartItems->groupBy(function ($item) {
                        return $item->product_id . '-' . $item->option_id;
                    });

                    $getPriceColumn = UserHelper::getUserPriceColumn(false, $getSelectedContact->user_id);

                    // Process the cart items
                    $cart_data = [];
                    foreach ($cartItemsGrouped as $groupKey => $groupItems) {
                        $cartItem = $groupItems->first();

                        // Fetch the product pricing
                        $productPricing = Pricingnew::where('option_id', $cartItem['option_id'])->first();
                        $productPrice = $productPricing->$getPriceColumn ?? $productPricing['sacramentoUSD'] ?? $productPricing['retailUSD'] ?? 0;

                        // Merge the quantities of grouped items
                        $totalQuantity = $groupItems->sum('quantity');

                        // Update or create cart item with the current active contact_id and price
                        Cart::updateOrCreate(
                            [
                                'user_id' => $user_id,
                                'product_id' => $cartItem['product_id'],
                                'option_id' => $cartItem['option_id'],
                                'contact_id' => $active_contact_id,
                            ],
                            [
                                'quantity' => $totalQuantity,
                                'price' => $productPrice
                            ]
                        );

                        // Store updated cart data for session
                        $cart_data[$cartItem['qoute_id']] = [
                            "product_id" => $cartItem['product_id'],
                            "name" => $cartItem['name'],
                            "quantity" => $totalQuantity,
                            "price" => $productPrice,
                            "code" => $cartItem['code'],
                            "image" => $cartItem['image'],
                            'option_id' => $cartItem['option_id'],
                            "slug" => $cartItem['slug'],
                            "qoute_id" => $cartItem['qoute_id'],
                            'user_id' => $cartItem['user_id'],
                            'contact_id' => $cartItem['contact_id'],                            
                        ];

                        // Delete the duplicate items (those after the first one in the group)
                        Cart::where('user_id', $user_id)
                        ->where('product_id', $cartItem['product_id'])
                        ->where('option_id', $cartItem['option_id'])
                        ->where('contact_id', $active_contact_id)
                        ->where('id', '!=', $cartItem['id']) // Exclude the updated one
                        ->delete();
                    }

                    // Store updated cart in session
                    Session::forget('cart');
                    Session::put('cart', $cart_data);
                }
            }
            
        }
        

        return response()->json([
            'status' => '204',
            'message' => 'Company Switch Successfully !'
        ]);
    }

    public static function getUserPriceColumn($is_admin = false , $user_id = null) {
        $price_column = null;
        $default_price_column = AdminSetting::where('option_name', 'default_price_column')->first();
        if (!empty($default_price_column)) {
            $price_column = $default_price_column->option_value;
        }
        else {
            $price_column = 'sacramentoUSD';
        }

        if (empty($price_column)) {
            $price_column = 'retailUSD';
        }

        if ($is_admin === false) {

            $user_id = Auth::id();
        }

        if (empty($user_id)) {
            return $price_column;
        }

        $company = Session::get('contact_id');
        if (!empty($company)) {
            $contact = Contact::where('user_id', $user_id)
            ->where('contact_id', $company)
            ->orWhere('secondary_id', $company)
            ->first();

            if (!empty($contact)) {
                // if it's parent-id and secondary-id both exists then get price column from parent contact
                if (!empty($contact->parent_id) && !empty($contact->secondary_id)) {
                    $parent_contact = Contact::where('contact_id', $contact->parent_id)
                        ->where('status', '!=', 0)
                        ->first();

                    if (!empty($parent_contact->priceColumn)) {
                        $price_column = $parent_contact->priceColumn;
                    }
                }
                else {
                    $price_column = $contact->priceColumn;
                }
            }    
        }

        return lcfirst($price_column);
    }



