    public static function allowAiPricing($productOption)
    {
        // Retrieve the current cart entry for the product option (stock threshold limit)
        $cartEntry = Cart::where('product_id', $productOption->product_id)
                        ->where('option_id', $productOption->option_id)
                        ->first();

        // Fetch admin settings
        $enableSellingSetting      = AdminSetting::where('option_name', 'enable_selling_through_ai')
                                            ->where('option_value', 'Yes')
                                            ->first();
        $minStockThresholdSetting  = AdminSetting::where('option_name', 'minimum_stock_threshold')->first();
        $aiQuantityThresholdSetting = AdminSetting::where('option_name', 'ai_quantity_threshold_percentage')->first();

        // Convert settings to proper values
        $enableSellingThroughAI = !empty($enableSellingSetting);
        $minStockThreshold      = !empty($minStockThresholdSetting) ? intval($minStockThresholdSetting->option_value) : 0;
        $aiStockThresholdPerc   = !empty($aiQuantityThresholdSetting) ? intval($aiQuantityThresholdSetting->option_value) : 0;

        // Determine the actual stock value (using stockAvailable)
        $productActualStock = $productOption->stockAvailable;

        // Initialize threshold variables
        $thresholdPercentage   = 0;
        $aiStockThresholdValue = 0;

        // Calculate thresholds only if the actual stock meets the minimum threshold and is positive
        if ($productActualStock >= $minStockThreshold && $productActualStock > 0) {
            $thresholdPercentage   = round(($aiStockThresholdPerc / $productActualStock) * 100);
            $aiStockThresholdValue = round(($thresholdPercentage / 100) * $productActualStock);
        }

        // If cart is empty, treat its quantity as 0
        $cartQuantity = !empty($cartEntry) ? intval($cartEntry->quantity) : 0;

        // Determine if AI pricing is allowed by combining all conditions
        $allowAIPrice = $enableSellingThroughAI 
            && $productActualStock > 0 
            && $aiStockThresholdValue > 0 
            && (intval($productOption->sold_by_ai) < $aiStockThresholdValue)
            && ($cartQuantity < $aiStockThresholdValue);


        // dd($allowAIPrice);

        return $allowAIPrice;
    }

    
    public static function switch_price_tier(Request $request) {
        $user_id = auth()->id();
        $company = session()->get('company');
        $contact_id = session()->get('contact_id');
    
        // Step 1: Determine cart items based on session or database
        if (!$user_id) {
            // Unauthenticated users: Retrieve cart from session
            $cart_items = $request->session()->get('cart');
        } elseif (empty($company) && $user_id) {
            // Authenticated users without a company: Retrieve cart from DB
            $cart_items = Cart::where('user_id', $user_id)->get();
        } else {
            // Authenticated users with a company or contact: Retrieve cart items based on contact ID
            $getSelectedContact = Contact::where('contact_id', $contact_id)
                ->orWhere('secondary_id', $contact_id)
                ->where('user_id', $user_id)
                ->first();
    
            // Initialize variables
            $cart_items = [];
            $cartItems = Cart::where('user_id', $getSelectedContact->user_id)
                ->where('contact_id', $contact_id)
                ->get();
            
            // Step 2: Get the appropriate price column for the user
            $getPriceColumn = UserHelper::getUserPriceColumn(false, $getSelectedContact->user_id);
    
            // Step 3: Process each cart item and update prices
            foreach ($cartItems as $cartItem) {
                // Fetch product pricing
                $productPricing = Pricingnew::where('option_id', $cartItem['option_id'])->first();
                $productOption = ProductOption::where('option_id', $cartItem['option_id'])
                ->where('product_id', $cartItem['product_id'])
                ->first();
                $allowAiPricing = UserHelper::allowAiPricing($productOption);

                if ($allowAiPricing) {
                    if (($productPricing->enable_ai_price == 1) && ($productPricing['aiPriceUSD'] != '0') && ($productPricing['aiPriceUSD'] > 0)) {
                        $productPrice  = $productPricing['aiPriceUSD'];
                    } else {
                        $productPrice = $productPricing->$getPriceColumn ?? 0;
                    }
                }

                else {
                    $productPrice = $productPricing->$getPriceColumn ?? 0;
                }
    
                // Fallback pricing logic
                if ($productPrice == 0) {
                    $productPrice = $productPricing['sacramentoUSD'] ?? $productPricing['retailUSD'];
                }
    
                // Update the cart price if found
                $cart = Cart::where('user_id', $user_id)->where('product_id', $cartItem['product_id'])->first();
                if ($cart) {
                    $cart->price = $productPrice;
                    $cart->save();
                }
    
                // Build the updated cart items array
                $cart_items[$cartItem['qoute_id']] = [
                    "product_id" => $cartItem['product_id'],
                    "name" => $cartItem['name'],
                    "quantity" => $cartItem['quantity'],
                    "price" => $cart->price ?? $productPrice,  // Ensure price is set
                    "code" => $cartItem['code'],
                    "image" => $cartItem['image'],
                    "option_id" => $cartItem['option_id'],
                    "slug" => $cartItem['slug'],
                    'qoute_id' => $cartItem['qoute_id'],
                    'user_id' => $cartItem['user_id'],
                    'contact_id' => $cartItem['contact_id'],
                ];
            }
    
            // Step 4: Store updated cart items back in the session
            session()->forget('cart');
            session()->put('cart', $cart_items);
        }
    
        // Return the final cart items array
        return $cart_items;
    }

    public static function validateAddress($address)
    {
        $client = new \GuzzleHttp\Client();
        $address_validator_key = config('services.google_address_validator.address_validator_google_key');
        $response = $client->get('https://maps.googleapis.com/maps/api/geocode/json', [
            'query' => [
                'address' => $address,
                'key' => $address_validator_key,
            ]
        ]);
        $data = json_decode($response->getBody(), true);

        return $data['status'];
    }

    public static function  cleanDescription($description) {
        // Step 1: Decode HTML entities
        $decodedDescription = html_entity_decode($description);
    
        // Step 2: Remove specific unwanted characters
        // List of unwanted characters to remove
        $unwantedCharacters = ['&nbsp;', ':', '', '?', '�', '*']; // Add any other unwanted characters here
    
        // Replace unwanted characters with a space
        $cleanedDescription = str_replace($unwantedCharacters, ' ', $decodedDescription);
    
        // Step 3: Remove extra spaces, tabs, and newlines
        $cleanedDescription = preg_replace('/\s+/', ' ', $cleanedDescription);
    
        // Step 4: Sanitize the content
        // Allow specific HTML tags and attributes
        $allowedTags = '<p><a><b><i><strong><em><br><ul><ol><li>';
        $cleanedDescription = strip_tags($cleanedDescription, $allowedTags);
    
        return trim($cleanedDescription); // Trim any leading or trailing spaces
    }


    
    
